/**
 * [DataMap 생성하기] 클릭 시
 * DataMap 생성하기
 */
scwin.btn_ex1_onclick = function (e) {
    var jsnOption;

    //로그 출력
    $c.frame.printExampleLog("** [실행1] - DataMap 생성하기 scwin.btn_ex1_onclick 실행 시작 **", txa_log, false);

    //DataMap 속성 정의
    jsnOption = {
        "id": "dma_dynamic1",  //생성할 DatMap의 ID입니다.
        "type": "dataMap",     //생성할 데이터객체의 타입입니다. dataMap 고정.
        "option": {
            "baseNode": "map"
        },
        "keyInfo": [
            {
                "id": "name",
                "name": "성명",
                "dataType": "text"
            }, {
                "id": "address",
                "name": "주소",
                "dataType": "text"
            }
        ]
    };

    //1. DataCollection 생성하기
    $p.data.create(jsnOption);

    //로그 출력
    $c.frame.printExampleLog("> 1단계 DataMap 생성 완료", txa_log, false);

    //2. 생성된 객체에 Data 할당하기
    //옵션에 적용한 id로 직접 접근이 가능합니다.
    dma_dynamic1.setJSON({
        "name": "Inswave Systems",
        "address": "서울시 강서구 공항대로 247, 퀸즈파크나인 C동 12층"
    });

    /*
        //또는 아래와 같이 $p.getComponentById API로 객체를 반환 받아 제어할 수 있습니다.
        let cmpDataMap = $p.getComponentById("dma_dynamic1");
        cmpDataMap.setJSON({
            "name" : "Inswave Systems",
            "address" : "서울시 강서구 공항대로 247, 퀸즈파크나인 C동 12층"
        });
    */

    //로그 출력
    $c.frame.printExampleLog("> 2단계 DataMap setJSON 호출 완료", txa_log, false);

    let strData = JSON.stringify(dma_dynamic1.getJSON());

    //로그 출력
    $c.frame.printExampleLog(strData, txa_log, false);
    $c.frame.printExampleLog("> 3단계 DataMap getJSON 호출 완료", txa_log, false);

    //로그 출력
    $c.frame.printExampleLog("** [실행1] - DataMap 생성하기 scwin.btn_ex1_onclick 실행 종료 **", txa_log, false);

};

